# Step 2: Add risk calculation to working sentiment extraction
results = []
items = _input.all()

for i, item in enumerate(items):
    data = item.json

    # Extract sentiment scores (this is working!)
    positive = 0
    negative = 0
    neutral = 0

    for sentiment in data:
        if sentiment['label'] == 'positive':
            positive = sentiment['score']
        elif sentiment['label'] == 'negative':
            negative = sentiment['score']
        elif sentiment['label'] == 'neutral':
            neutral = sentiment['score']

    # NEW: Add risk calculation
    sentiment_risk = negative  # Higher negative = higher risk

    # Simple risk level
    if sentiment_risk > 0.7:
        risk_level = 'CRITICAL'
    elif sentiment_risk > 0.5:
        risk_level = 'HIGH'
    elif sentiment_risk > 0.3:
        risk_level = 'MEDIUM'
    elif sentiment_risk > 0.1:
        risk_level = 'LOW'
    else:
        risk_level = 'MINIMAL'

    # Determine dominant sentiment
    if positive > max(negative, neutral):
        dominant = 'positive'
    elif negative > neutral:
        dominant = 'negative'
    else:
        dominant = 'neutral'

    result = {
        'item_number': i,
        'sentiment_positive': round(positive, 3),
        'sentiment_negative': round(negative, 3),
        'sentiment_neutral': round(neutral, 3),
        'dominant_sentiment': dominant,
        'risk_score': round(sentiment_risk, 3),
        'risk_level': risk_level
    }

    results.append(result)


return results
