# Mode: Run Once for All Items
import statistics
from datetime import datetime

incoming = _input.all()   # all items from Merge

# ---------- helpers (safe for JsProxy / dict) ----------
def k(obj):
    try:
        return list(obj.keys())
    except Exception:
        return []

def g(obj, key, default=None):
    try:
        return obj[key]
    except Exception:
        try:
            return obj.get(key, default)
        except Exception:
            return default

def unwrap(x):
    try:
        j = x.json if hasattr(x, "json") else x
        # unwrap {"json": {...}} if present
        try:
            if "json" in j and (hasattr(j["json"], "keys") or hasattr(j["json"], "__len__")):
                return j["json"]
        except Exception:
            pass
        return j
    except Exception:
        return x

def is_market(d):
    # Alpha Vantage daily series objects contain "Time Series (Daily)"
    try:
        return any(str(key).strip() == "Time Series (Daily)" for key in k(d))
    except Exception:
        return False

def get_symbol(d):
    meta = g(d, "Meta Data", {})
    s = g(meta, "2. Symbol") or g(meta, "2. Symbol ")
    return str(s).strip() if s else None

# ---------- bucket incoming by symbol ----------
buckets = {}              # sym -> {"market": {...}}
sentiment_list = None     # list of {label, score} (shared across symbols for demo)
industrial = None
horizon_default = 30

for it in incoming:
    d = unwrap(it)

    if is_market(d):
        sym = get_symbol(d)
        if sym:
            buckets.setdefault(sym, {})["market"] = d

    # FinBERT result looks like a list of dicts with "label"/"score"
    elif isinstance(d, list) and d and isinstance(d[0], dict) and "label" in d[0]:
        if sentiment_list is None:
            sentiment_list = d  # reuse same sentiment for all tickers (simple demo)

    # optional industrial metrics object
    elif g(d, "current_metrics", None) is not None:
        industrial = d

    # pull horizon if any upstream item carries it
    h = g(d, "forecast_horizon_days", None)
    if h is not None:
        try:
            horizon_default = int(h)
        except Exception:
            pass

outputs = []

# ---------- build one output per symbol ----------
for sym, data in buckets.items():
    mk = data.get("market")
    if not mk:
        continue

    # locate the time series block
    ts_key = next((key for key in k(mk) if str(key).strip() == "Time Series (Daily)"), None)
    if not ts_key:
        continue
    ts = mk[ts_key]

    # collect last ~30 closes
    days = sorted([str(day) for day in k(ts)], reverse=True)[:30]
    closes = []
    for day in days:
        try:
            closes.append(float(g(ts[day], "4. close")))
        except Exception:
            pass
    if len(closes) < 2:
        continue

    # daily simple returns from closes
    rets = []
    for i in range(len(closes) - 1):
        try:
            rets.append((closes[i] - closes[i+1]) / closes[i+1])
        except Exception:
            pass

    vol = statistics.stdev(rets) if len(rets) > 1 else 0.0
    change = (closes[0] - closes[-1]) / closes[-1] if closes[-1] else 0.0
    trend = "up" if change > 0.02 else ("down" if change < -0.02 else "flat")

    processed = {
        "timestamp": datetime.now().isoformat(),
        "symbol": sym,
        "data_sources": {
            "market": {
                "latest_price": closes[0],
                "price_change_pct": round(change * 100, 2),  # percent, for display
                "trend": trend,
            }
        },
        "volatility": {
            "value": round(vol, 4),                                      # daily stdev (decimal)
            "level": "high" if vol > 0.03 else ("medium" if vol > 0.015 else "low"),
            "risk_score": round(vol * 100, 2),                            # 0..100 style
        },
        "quality_metrics": {"market_ok": True},
        "features": {},
        "forecast_horizon_days": horizon_default,
    }

    # --- sentiment (shared across symbols for the demo) ---
    if sentiment_list:
        scores = {}
        for row in sentiment_list:
            try:
                scores[row["label"]] = row["score"]
            except Exception:
                pass
        if scores:
            dom = max(scores, key=scores.get)
            processed["data_sources"]["sentiment"] = {
                "dominant": dom,
                "confidence": scores[dom],
                "value": 1 if dom == "positive" else (-1 if dom == "negative" else 0),
            }
            processed["quality_metrics"]["sentiment_ok"] = True

    # --- optional industrial metrics ---
    if industrial and g(industrial, "current_metrics", None) is not None:
        m = g(industrial, "current_metrics", {})
        processed["data_sources"]["industrial"] = {
            "production": g(m, "avg_production_7d"),
            "quality":    g(m, "avg_quality_7d"),
            "efficiency": g(m, "avg_efficiency_7d"),
            "downtime":   g(m, "total_downtime_7d"),
        }
        processed["quality_metrics"]["industrial_ok"] = True

    # --- features used by Code2 ---
    f = {}
    if processed["data_sources"].get("sentiment"):
        s = processed["data_sources"]["sentiment"]
        f["sentiment"] = s["value"]
        f["sentiment_conf"] = s["confidence"]

    mkt = processed["data_sources"]["market"]
    f["price_momentum"] = mkt["price_change_pct"] / 100.0          # back to decimal
    f["market_trend"]   = 1 if mkt["trend"] == "up" else (-1 if mkt["trend"] == "down" else 0)
    f["volatility"]     = processed["volatility"]["value"]
    f["risk"]           = processed["volatility"]["risk_score"] / 100.0

    if processed["data_sources"].get("industrial"):
        ind = processed["data_sources"]["industrial"]
        # gentle scaling to dimensionless features
        f["production"] = (ind["production"] or 1000.0) / 1000.0
        f["quality"]    = ind["quality"] or 0.9
        f["efficiency"] = ind["efficiency"] or 0.85

    processed["features"] = f

    ok = sum(1 for kq in ("sentiment_ok", "market_ok", "industrial_ok") if processed["quality_metrics"].get(kq))
    processed["quality_metrics"]["overall_pct"] = (ok / 3) * 100
    processed["quality_metrics"]["ready"] = ok >= 2

    outputs.append({"json": processed})

# emit one item per symbol
return outputs

