// --- Inputs ---
const companies = $json["companies"];
const singleCompany = $node["Set Variables"]?.json?.company || null; // optional single company name

// === knobs you can tweak ===
const LOOKBACK_HOURS = 72;
const USE_KEYWORDS = false;
const USE_DOMAINS = false;

const DOMAINS = "reuters.com,bloomberg.com,cnbc.com,wsj.com";
const EXTRA = "(earnings OR revenue OR guidance OR outlook OR profit OR loss OR upgrade OR downgrade OR lawsuit OR acquisition)";

const LANG = "en";
const SORT = "publishedAt";
const PAGE_SIZE = 100;

// Helper function
function isoHoursAgo(hours) {
    const date = new Date();
    date.setHours(date.getHours() - hours);
    return date.toISOString();
}

// Helper to build payload
function buildPayload(name, symbol = null, batch = 1, totalBatches = 1) {
    const baseQ = symbol ? `${symbol} OR ${name}` : name;
    const q = USE_KEYWORDS ? `(${baseQ}) AND ${EXTRA}` : baseQ;

    const payload = {
        q: q,
        from: isoHoursAgo(LOOKBACK_HOURS),
        language: LANG,
        sortBy: SORT,
        pageSize: PAGE_SIZE,
        company: symbol || name,
        batch: batch,
        total_batches: totalBatches
    };

    if (USE_DOMAINS) {
        payload.domains = DOMAINS;
    }

    return { json: payload };
}

const out = [];

if (singleCompany) {
    // Lookup symbol from the companies list
    const matched = companies.find(c => c.name?.trim().toLowerCase() === singleCompany.trim().toLowerCase());
    const symbol = matched?.symbol || null;
    out.push(buildPayload(singleCompany, symbol));
} else {
    companies.forEach((c, idx) => {
        const name = c.name?.trim() || "";
        const symbol = c.symbol?.trim() || "";
        out.push(buildPayload(name, symbol, idx + 1, companies.length));
    });
}

return out;

// Process NewsAPI response
const newsData = $json;

if (newsData.articles && Array.isArray(newsData.articles)) {
  return newsData.articles.map(article => ({
    json: {
      title: article.title || '',
      description: article.description || '',
      content: article.content || '',
      url: article.url || '',
      source: article.source?.name || 'Unknown',
      publishedAt: article.publishedAt || '',
      processed_text: `${article.title || ''} ${article.description || ''}`.trim()
    }
  }));
} else {
  return [{json: {error: 'No articles found'}}];
}

