// Get all download results
const items = $input.all();
const config = items[0].json; // From Set Variables

// Find downloaded files by checking which items have 'body' property
const downloads = items.slice(1); // Skip the config item

const files = {
  'requirements.txt': downloads.find(item => item.json.url?.includes('requirements.txt'))?.json.body,
  'edgar_fetcher.py': downloads.find(item => item.json.url?.includes('edgar_fetcher'))?.json.body,
  'financial_analyzer.py': downloads.find(item => item.json.url?.includes('financial_analyzer'))?.json.body,
  'narrative_parser.py': downloads.find(item => item.json.url?.includes('narrative_parser'))?.json.body
};

// Check for missing files
const missing = Object.entries(files)
  .filter(([name, content]) => !content)
  .map(([name]) => name);

if (missing.length > 0) {
  throw new Error(`Failed to download: ${missing.join(', ')}`);
}

// Write files using Node.js fs
const fs = require('fs');
const { execSync } = require('child_process');

Object.entries(files).forEach(([filename, content]) => {
  const filepath = `${config.work_dir}/scripts/${filename}`;
  fs.writeFileSync(filepath, content, 'utf8');
});

// Make scripts executable
execSync(`chmod +x ${config.work_dir}/scripts/*.py`);

return [{
  json: {
    ...config,
    files_ready: true,
    downloaded_files: Object.keys(files)
  }
}];

// Parse EDGAR output
const stdout = $input.first().json.stdout;
const stderr = $input.first().json.stderr;
const exitCode = $input.first().json.exitCode;
const config = $node['Set Variables'].json;

if (exitCode !== 0) {
  return [{
    json: {
      success: false,
      error: `EDGAR script failed: ${stderr}`,
      ticker: config.ticker
    }
  }];
}

let result = {};
try {
  const jsonMatch = stdout.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    result = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found in output');
  }
} catch (e) {
  return [{
    json: {
      success: false,
      error: `Failed to parse EDGAR output: ${e.message}`,
      raw_output: stdout
    }
  }];
}

if (!result.success) {
  return [{
    json: {
      success: false,
      error: result.error || 'EDGAR processing failed',
      ticker: config.ticker
    }
  }];
}

return [{
  json: {
    ...result,
    work_dir: config.work_dir,
    ticker: config.ticker,
    user_agent: config.user_agent
  }
}];

