// Combine all results
const edgarData = $input.item(0).json;
const financialData = $input.item(1).json;
const narrativeData = $input.item(2).json;

// Check if any step failed
if (!edgarData.success || !financialData.success || !narrativeData.success) {
  const errors = [];
  if (!edgarData.success) errors.push(`EDGAR: ${edgarData.error}`);
  if (!financialData.success) errors.push(`Financial: ${financialData.error}`);
  if (!narrativeData.success) errors.push(`Narrative: ${narrativeData.error}`);

  return [{
    json: {
      success: false,
      error: errors.join('; '),
      ticker: edgarData.ticker || financialData.ticker || narrativeData.ticker
    }
  }];
}

const ticker = edgarData.ticker;
const company_name = edgarData.company_name || 'Unknown Company';

// Extract financial highlights
const financial = financialData.financial_data.data || financialData.financial_data;
const narrative = narrativeData.narrative_data;

const report = {
  success: true,
  ticker: ticker,
  company_name: company_name,
  analysis_timestamp: new Date().toISOString(),

